	public function flushComments()
	{
		return Doctrine_Query::create()->delete('Commentaire c')->where("c.content_type = 'feedback'")->andWhere("c.content_id = ?", $this->getId())->execute();
	}
	
	public function getCountVote()
	{
		return Doctrine_Query::create()->from('CommentaireVote v')->where("v.content_type = 'feedback'")->andWhere("v.content_id = ?", $this->getId())->count();
	}
	
	public function getCountComment()
	{
		return Doctrine_Query::create()->from('Commentaire c')->where("c.content_type = 'feedback'")->andWhere("c.content_id = ?", $this->getId())->count();
	}
}
